sprites.onOverlap(SpriteKind.Player, SpriteKind.recycleable, function (sprite, otherSprite) {
    if (playStart == true) {
        info.changeScoreBy(1)
        sprites.destroy(otherSprite)
        mySprite.startEffect(effects.confetti, 500)
        music.play(music.melodyPlayable(music.baDing), music.PlaybackMode.UntilDone)
    }
})
sprites.onOverlap(SpriteKind.Player, SpriteKind.PaperNPC, function (sprite, otherSprite) {
    DialogueMode = true
    game.showLongText("What waste goes into the Paper bin? ", DialogLayout.Center)
    story.showPlayerChoices("Solid plastic, metal and glass", "PAPER, like magazines and cardboard", "Food Waste", "soft plastics, clothing and others")
    DialogueMode = false
    if (story.checkLastAnswer("Solid plastic, metal and glass")) {
        info.changeScoreBy(-5)
    } else if (story.checkLastAnswer("PAPER, like magazines and cardboard")) {
        info.changeScoreBy(1)
        Paper_list.setKind(SpriteKind.Complete)
        sprites.destroy(Paper_list)
    } else if (story.checkLastAnswer("Food Waste")) {
        info.changeScoreBy(-5)
    } else if (story.checkLastAnswer("soft plastics, clothing and others")) {
        info.changeScoreBy(-5)
    } else {
    	
    }
    pause(1000)
})
